mapper.update:
update member
set username=#{username}
where member_id = #{memberId}

update member
set birthdate=#{birthdate}
where member_id = #{memberId}

update member
set mobile=#{mobile}
where member_id = #{memberId}

update member
set zipcode=#{zipcode},
    address=#{address},
    sub_address=#{subAddress}
where member_id = #{memberId}

update member
set bio=#{bio}
where member_id = #{memberId}

update member
set password=#{password},
    update_date=#{updateDate}
where member_id = #{memberId}

update member
set profile_img=#{profileImg}
where member_id = #{memberId}

update member
set host_grade=#{hostGrade},
    role=#{role}
where member_id = #{memberId}
