mapper.select:
select * from reservation where member_id = #{memberId} and update_target is null and status = #{status}
or status = 'update-no'
order by check_in

mapper.update:
update reservation
set status = 'update'
where reservation_id = #{reservationId}

update reservation
set status = #{status}
where update_target = #{reservationId}

update reservation
set status = 'update-no'
where reservation_id = #{reservationId}

mapper.select:
select reservation_id, room_id, adult, kid, infant, check_in, check_out
from reservation
where update_target = #{reservationId}

mapper.update:
update reservation
set cancel_date = #{cancelDate},
    status      = #{status}
where reservation_id = #{reservationId}
