mapper.select:
select *
from reservation
where member_id = #{memberId}
  and status = #{status}
  and update_target is null
order by check_in

select review.reply_content, review.reply_reg_date
from review
       join reservation r on r.reservation_id = review.reservation_id
       join payment p on p.reservation_id = r.reservation_id
where r.room_id = #{roomId}
  and member_id = #{memberId};

mapper.update:
update reservation
set status = 'update'
where reservation_id = #{reservationId}

update reservation
set cancel_date = #{cancelDate},
    status      = #{status}
where reservation_id = #{reservationId}
